import React, { useState } from 'react'
import SearchBar from './components/SearchBar'
import Popup from './components/Popup'

function App() {

  const [selected,setSelected] = useState([])

  const openPopup = (movieDetail) => {
    setSelected(movieDetail)
  }

  const closePopup = ()=>{
    setSelected([])
  }

  return (
    <div className="App">
      <h1> Movie Search </h1>

      <div>
       <SearchBar openPopup={openPopup}/>
       {(selected !== []) ? <Popup selected={selected} closePopup={closePopup}/> : false}


      </div>

    </div>
  );
}

export default App;
