import React, {useState} from 'react'
import Popup from './Popup'



function SearchBar({openPopup}) {

    const [title, setTitle] = useState('')
    const [movies, setMovies] = useState([])
    const [selected, setSelected] = useState(null)


    const searchMovie =  async(event) => {
        event.preventDefault();

        console.log(`title is ${title}`)


       const url = `https://api.themoviedb.org/3/search/movie?api_key=c7fb2bfe8aa168f9562481b40e40c26a&language=en-US&query=${title}&include_adult=false`

       const urlAPI = `https://movie-database-imdb-alternative.p.rapidapi.com/?s=${title}&page=1&r=json`  

       //    const urlAPI = ("https://movie-database-imdb-alternative.p.rapidapi.com/?s=Avengers%20Endgame&page=1&r=json", {
    //     "method": "GET",
    //     "headers": {
    //         "x-rapidapi-key": "dcc87032famshc1b79ddcc901dc4p140abfjsn05b22a78edfd",
    //         "x-rapidapi-host": "movie-database-imdb-alternative.p.rapidapi.com"
    //     }
    // })

       try{
        //    const res = await fetch(url)
           const res = await fetch(urlAPI, {
                                   "method": "GET",
                                   "headers": {
                                             "x-rapidapi-key": "dcc87032famshc1b79ddcc901dc4p140abfjsn05b22a78edfd",
                                             "x-rapidapi-host": "movie-database-imdb-alternative.p.rapidapi.com"

           }})
           const data = await res.json();


           console.log('return data', data.search)
           setMovies(data.results)


       }catch(err){
           console.error(err)
       }

    }

    const displayMovie = (movie) => {
        console.log('showing this movie', movie)
        setSelected(movie)
        console.log(selected)

    }



    return (

        <>
        <form className='form' onSubmit={searchMovie}>
            <input className='input' type='text' placeholder='type a movie title...'
                   value={title} onChange={(event)=> setTitle(event.target.value)}
            />
            <button className='button' type='submit'> Search</button>
        </form>

        <div className='movies-list'> 
       {movies.map(movie =>


          <div className='movie-card' key={movie.id} onClick={()=> displayMovie(movie)}>
          <h2>{movie.title}</h2>
          <img src={`http://image.tmdb.org/t/p/w185_and_h278_bestv2/${movie.poster_path}`} 
                alt={`${movie.title}`}/>  
          <p>{movie.overview}</p>

          {(selected === movie ) ? <Popup selected={selected}/> : false}


         </div>






       )}

        </div>

        </>


    )
}

export default SearchBar
