import React, {useState} from 'react'

export default async function Popup ({selected}) {
    console.log('popup function', selected)

    const [movieID, setMovieID] = useState(selected.imdbID)
    const [currentMovie, setCurrentMovie] = useState({})

    console.log('id by state', movieID)

    const url = `https://movie-database-imdb-alternative.p.rapidapi.com/?i=${movieID}&r=json`  

       try{
        //    const res = await fetch(url)
           const res = await fetch(url, {
                                   "method": "GET",
                                   "headers": {
                                             "x-rapidapi-key": "dcc87032famshc1b79ddcc901dc4p140abfjsn05b22a78edfd",
                                             "x-rapidapi-host": "movie-database-imdb-alternative.p.rapidapi.com"

           }})
           const data = await res.json();
           console.log('return dataaaaaaa', data)
           setCurrentMovie(data)

       }catch(err){
           console.error(err)
       }


    return (
        <section>
            <div> 
            <h1>Movie By Id {selected.Title}</h1>
            <h3>Release Date : {selected.Year}</h3>
            <h3>{selected.vote_average}</h3>

            <button className='close-button'>Close</button>
            </div>
        </section>
    )
}
